(2132, 'La Femme du chef de gare', NULL, 'inconnue', '1h 52min', NULL, 1976, 'Malgre les diverses aventures de sa jeune femme Hanni et les cancans de la ville dont il n\'a cure, Xavier Bolwieser, chef de gare de son etat, continue de l\'aimer avec passion.', NULL, 2, 0, 'Drame', '', '', 'Bolwieser', ''),
(2133, 'Top Gun', 'http://a69.g.akamai.net/n/69/10688/v1/img5.allocine.fr/acmedia/medias/nmedia/18/36/26/94/18862989.jpg', '17 Septembre 1986', '1h 49min', NULL, 1985, 'Les amours d\'un professeur et d\'un pilote hors-pair et tête brûlée dans une école de haut niveau, \"Top-Gun\", de l\'aéronavale US.', NULL, 3, 1, 'Aventure', '', '', '', 'United International Pictures (UIP)'),
