(2203, 'Themroc', NULL, 'inconnue', '1h 45min', NULL, 1973, 'Une transformation physique et biologique va bouleverser la vie d\'un vieux garcon partageant son temps entre sa mere et son travail.', NULL, 4, 0, 'Fantastique', '', '', '', ''),
(2204, 'Jeanne Dielman 23, quai du Commerce, 1080 Bruxelles', NULL, 'inconnue', '3h 18min', NULL, 1975, 'Le quotidien d\'une mère de famille qui se livre occasionnellement à la prostitution.', NULL, 4, 0, 'Drame', '', '', '', ''),
(2205, 'Aloïse', NULL, 'inconnue', '1h 55min', NULL, 1975, 'L\'histoire véridique d\'Aloïse, aliénée, enfermée toute sa vie, qui a fini par s\'exprimer par la peinture.', NULL, NULL, 0, 'Drame', '', '', '', ''),
