(3053, 'La Vallée du bonheur', 'http://a69.g.akamai.net/n/69/10688/v1/img5.allocine.fr/acmedia/medias/nmedia/18/36/26/34/18466605.jpg', 'inconnue', '2h 25min', NULL, 1968, 'Finian, un mystérieux Irlandais, arrive dans une ville du sud des Etats-Unis accompagné de sa charmante fille Susan.', NULL, 2, 0, 'Comédie dramatique, Comédie musicale', '', '', 'Finian\'s rainbow', ''),
(3054, 'Blinkity Blanck', NULL, 'inconnue', '5min', NULL, 1955, 'Film d\'animation realise sans camera, grave sur la pellicule. Palme d\'or du court metrage, Cannes 1955.', NULL, NULL, 0, 'Court métrage', '', '', '', ''),
