(3519, 'Essai de reconstitution des 46 jours qui precedent la mort de Francoise Guiniou', NULL, 'inconnue', '20min', NULL, 1971, 'Dans une cite de banlieue, une femme s\'enferme avec ses deux enfants et se laisse mourir.', NULL, NULL, 0, 'Court métrage', '', '', '', ''),
(3520, 'Les Coeurs verts', NULL, 'inconnue', '1h 28min', NULL, 1966, 'Deux jeunes delinquants de Nanterre, tout juste sortis de prison, tentent leur reinsertion, tirailles entre leurs familles, leurs copains et la necessite de trouver du travail.', NULL, NULL, 0, 'Comédie dramatique', '', '', '', ''),
