(7464, 'Simple men', 'http://a69.g.akamai.net/n/69/10688/v1/img5.allocine.fr/acmedia/medias/nmedia/18/36/15/29/18456161.jpg', 'inconnue', '1h 44min', NULL, 1992, 'L\'histoire de deux freres fort differents, a la recherche d\'un hypothetique pere, ancien gauchiste disparu, tous deux pris par une liaison amoureuse compliquee et incertaine.', NULL, 4, 0, 'Comédie dramatique', '', '', '', ''),
(7465, 'Un Louveteau parmi les hommes', NULL, 'inconnue', '1h 44min', NULL, 1988, 'Samat qui a apprivoise un louveteau va tout tenter pour sauver l\'animal de la convoitise d\'un chasseur qui veut l\'empailler.', NULL, NULL, 0, 'Comédie dramatique', '', '', 'Un louveteau parmi les hommes', ''),
