(9134, 'Turkish delices', 'http://a69.g.akamai.net/n/69/10688/v1/img5.allocine.fr/acmedia/medias/nmedia/18/35/56/53/18407059.jpg', 'inconnue', '1h 52min', NULL, 1973, 'Un peintre anti-bourgeois d\'Amsterdam vit un amour passionne avec une jeune fille.', NULL, 4, 0, 'Comédie dramatique', '', '', 'Turks fruit', ''),
(9135, 'Au nom du Christ', NULL, 'inconnue', '1h 20min', NULL, 1993, 'A travers l\'histoire d\'un porcher touche par la grace et d\'une belle jeune femme devenue folle sous l\'emprise d\'un sorcier, \"Au nom du Christ\" est une fable tragico-satirique sur l\'invasion des sectes en Afrique.', NULL, NULL, 0, 'Comédie dramatique', '', '', '', ''),
