(10229, 'Clean, Shaven', NULL, '14 Juin 1995', '1h 19min', NULL, 1994, 'Ce film dépeint les tourments d\'un schizophrène à la recherche de sa fille, et leurs brèves retrouvailles.', NULL, 4, 0, 'Drame', '16 ans', '', '', ''),
(10230, 'La Maison de lave', 'http://a69.g.akamai.net/n/69/10688/v1/img5.allocine.fr/acmedia/medias/nmedia/18/36/40/99/18481843.jpg', '15 Mars 1995', '1h 50min', NULL, 1994, 'En cherchant a sortir son compagnon d\'un cercle infernal, une jeune femme l\'entraine encore plus loin vers la mort.', NULL, NULL, 0, 'Drame', '', '', 'Casa de lava', '');
