(11683, 'Mayerling', NULL, 'inconnue', '2h 10min', NULL, 1968, 'A Vienne, Rodolphe, prince heritier mal marie, tombe amoureux d\'une baronne et prend le parti des etudiants contestataires.', NULL, 3, 0, 'Historique', '', 'Mayerling (1936)', '', 'Valoria Films'),
(11685, 'La Duchesse des bas-fonds', NULL, 'inconnue', '1h 28min', NULL, 1944, 'En devenant par hasard le modele de Gainsborough, une jeune prostituee attire l\'attention de la haute societe.', NULL, NULL, 0, 'Comédie dramatique', '', '', 'Kitty', ''),
