(12647, 'La Vie de Galilee', NULL, 'inconnue', '2h 8min', NULL, 1980, NULL, NULL, NULL, 0, 'Documentaire', '', '', '', ''),
(12648, 'Nenes et tango', NULL, 'inconnue', '52min', NULL, 1994, 'Une jeune Estonienne reve de changer son sort en dansant dans des spectacles de varietes a l\'etranger. Mais son professeur de danse est un mafieux et il profite des reves des jeunes filles.', NULL, NULL, 0, 'Comédie dramatique', '', '', '', ''),
