(15532, 'Dresse pour tuer', 'http://a69.g.akamai.net/n/69/10688/v1/img5.allocine.fr/acmedia/medias/nmedia/18/36/11/11/18453562.jpg', 'inconnue', '1h 24min', NULL, 1982, 'Un chien, dresse pour attaquer les Noirs, est recueilli par une jeune femme qui cherche a lui faire oublier la haine.', NULL, 4, 0, 'Drame', '', '', 'White Dog', ''),
(15533, 'Monstres du passe', NULL, 'inconnue', '5min', NULL, 1927, 'La fabrication d\'une maquette de tyrannosaure en argile.', NULL, NULL, 0, 'Court métrage', '', '', 'Monsters of the Past', ''),
