(16585, 'Solidarity Song : The Hanns Eisler Story', NULL, 'inconnue', '1h 20min', NULL, 1996, 'De Berlin a Hollywood, la vie de Hanns Eisler, tour a tour compositeur maudit et musicien officiel.', NULL, NULL, 0, 'Documentaire', '', '', '', ''),
(16586, 'Belcanto : Georges Thill et Joseph Schmidt', NULL, 'inconnue', '1h', NULL, NULL, 'Deux volets de la serie \"Belcanto\" consacree aux grands tenors. Portraits de Georges Thill (1897-1984) et de Joseph Schmidt (1904-1942) illustres d\'archives uniques.', NULL, NULL, 0, 'Documentaire', '', '', '', ''),
(16587, 'Vaclav Neumann, la vie d\'un chef d\'orchestre', NULL, 'inconnue', '54min', NULL, 1997, 'A travers de nombreuses archives et repetitions et des concerts de Vaclav Neumann avec le Philharmonique tcheque, la decouverte d\'une personnalite tres attachante de la direction d\'orchestre.', NULL, NULL, 0, 'Documentaire', '', '', '', ''),
