(19143, 'Rolling Stones at the Max', NULL, 'inconnue', '1h 20min', NULL, 1990, 'Film-concert géant réalisé à l\'occasion du Steel Wheels Tour des Rolling Stones, en 1990.', NULL, 4, 0, 'Documentaire', '', '', '', ''),
(19144, 'The Greatest Places', NULL, 'inconnue', '40min', NULL, 1998, 'Balade a travers le monde du Groenland a Madagascar, au Tibet, aux chutes d\'Iguacu, sur l\'Amazone, dans le desert de Namibie et au delta d\'Okavongo.', NULL, NULL, 0, 'Documentaire', '', '', '', ''),
(19145, 'L\'Egypte des pharaons', 'http://a69.g.akamai.net/n/69/10688/v1/img5.allocine.fr/acmedia/medias/nmedia/images/affiches/042804.jpg', 'inconnue', '40min', NULL, 1998, 'Un voyage au coeur d\'une civilisation antique, a la decouverte des mysteres de l\'Egypte pharaonique.', NULL, NULL, 0, 'Documentaire', '', '', '', ''),
