(23254, 'Premieres Vues', NULL, 'inconnue', '1h 5min', NULL, 1999, 'Cinq courts metrages sur le theme filmer l\'amour. \"Le coeur ne veut pas de repos\", \"Sous le signe du lien\", \"Scenes de balcon\", \"Maux d\'amour\", \"Deux petites amoureuses\".', NULL, NULL, 0, 'Court métrage', '', '', '', ''),
(23255, 'Vientiane', NULL, 'inconnue', '1h 6min', NULL, 1999, '\"S\'ouvrir a l\'imprevu... marcher dans la ville, marcher pour les rencontres. Faire des portraits simples et silencieux, mais pas muets... Les visages de Vientiane.\"', NULL, NULL, 0, 'Documentaire', '', '', '', ''),
