(29583, 'Le Complot', NULL, '07 Septembre 1988', '1h 55min', NULL, 1988, 'Film-fiction inspiré par l\'histoire de Popielusko, prêtre polonais idealiste et fervent militant du mouvement Solidarnosc, arrêté et tue par la Police Secrete en 1984.', NULL, 3, 0, 'Drame', '', '', 'To kill a Priest', 'Agence Méditerranéenne de Location de Films (A.M.L.F.)'),
(29584, 'Le Sicilien', 'http://a69.g.akamai.net/n/69/10688/v1/img5.allocine.fr/acmedia/medias/nmedia/18/63/28/41/18685063.jpg', '28 Octobre 1987', '2h 26min', NULL, 1987, '1943. Salvatore Giuliano veut faire de la Sicile un état libre...', NULL, 2, 0, 'Historique, Aventure', '', '', 'The Sicilian', 'Agence Méditerranéenne de Location de Films (A.M.L.F.)'),
