(31774, 'The Runner', NULL, 'inconnue', '1h 35min', NULL, 1999, 'Un jeune homme, passionné par le jeu, se retrouve rempli de dettes. Son oncle, pour l\'aider, lui trouve un travail auprès d\'un gangster.', NULL, NULL, 0, 'Thriller', '', '', '', ''),
(31775, 'No small affair', NULL, 'inconnue', '1h 42min', NULL, 1984, 'Charles Cummings, photographe, décide de suivre la carrière de Laura Victor, jeune chanteuse de rock. Il en tombe amoureux...', NULL, NULL, 0, 'Comédie, Romance', '', '', 'No Small Affair', ''),
